import "./NavBar.css";
//import react from "react";
import react, { useState, useEffect } from "react";
import NavItem from "./NavItem";

function NavBar() {
  const [lineWidth, setLineWidth] = useState(0);
  const [lineHeight, setLineHeight] = useState(0);
  const [navLineHeight, setNavLineHeight] = useState(0);
  const [navLineWidth, setNavLineWidth] = useState(0);
  const navLineWidth1 = 0;

  useEffect(() => {
    setLineWidth(80);
    setLineHeight(74);
  }, []);

  function hoverInNavItem(event) {
    console.log(event.target.textContent);
    // event.target.parentElement.firstChild.style.height = 20;
    setNavLineWidth(20);
    setNavLineHeight(20);

    // event.target.style.height = 20;
    // event.target.style.width = 20;
  }

  function hoverOutNavItem(event) {
    setNavLineWidth(0);
    setNavLineHeight(0);
    // event.target.style.height = 0;
    // event.target.style.width = 0;
  }

  return (
    <div>
      <div className="Top-boarder"></div>
      <div className="topLogo">
        <div className="longLine"></div>
        <div className="line" style={{ width: lineWidth }}></div>
      </div>
      <div className="App-header">
        <div className="verticleLine" style={{ height: lineHeight }}></div>
        <div className="column">
          <div>COFFEE</div>
          <div>CORNER</div>
        </div>
        <div className="break"></div>
        <div className="NavBar">
          <NavItem menu="MENU" />

          <div
            className="NavItem"
            onMouseOver={hoverInNavItem}
            onMouseOut={hoverOutNavItem}
          >
            <div className="leftBorder" style={{ height: navLineHeight }} />
            <div className="topBorder" style={{ width: navLineWidth }} />
            <div className="navBarText">LOCATIONS</div>
          </div>
          <div
            className="NavItem"
            onMouseOver={hoverInNavItem}
            onMouseOut={hoverOutNavItem}
          >
            <div className="leftBorder" style={{ height: navLineHeight }} />
            <div className="topBorder" style={{ width: navLineWidth }} />
            <div className="navBarText">ABOUT US</div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default NavBar;
